def Heat_Map():

    heat_map = np.zeros(shape=(len(m_values), 3))

    for m in range(len(m_values)):
        for i in range(1,4):
            heat_map[m][i - 1] = eval("L" + str(i)+ "[m]")


    limit = 6
    vmaxlog = 0

    label = [pow(10.0, i) for i in np.arange(vmaxlog - limit, vmaxlog)]
    vmax_num = pow(10, vmaxlog  + .25)
    vmin_num = pow(10, vmaxlog - limit)


    figure = plt.figure()
    axes = figure.add_subplot(1,1,1)
    
    yticks = np.arange(.5, 2*np.amax(m_values), 2)
    ylabel = np.arange(-1 * np.amax(m_values), np.amax(m_values), 2)

    # xticks = np.arange(.5, 13, 1)
    # xlabel = [534, 600, 650, 700, 750, 800, 850, 900, 950, 1000, 1068]

    xticks = np.arange(.5, 4, 1)
    xlabel = [534, 800, 1068]

    sns.set(font_scale=1.5)
    

    axes = sns.heatmap(heat_map, norm=colors.LogNorm(), yticklabels=ylabel, xticklabels=xlabel, linewidths=.1, 
    cmap="plasma", annot=False, cbar_kws={"ticks":label},  vmin=vmin_num, vmax=vmax_num)

    plt.xticks(xticks, fontsize=12, fontweight='bold', color = 'k' )
    plt.yticks(yticks, fontsize=12, fontweight='bold', color = 'k' )

    plt.ylabel('m', fontsize=18, fontweight='bold', color = 'k' )
    plt.xlabel('wavelength (nm)', fontsize=18, fontweight='bold' , color = 'k')

    plt.xlim(0, 3)
    plt.ylim(2, 19)

    plt.tight_layout()

    plt.savefig("Gebre-Bicircular2-Fig5.eps", format='eps')
    plt.savefig("Gebre-Bicircular2-Fig5.png", format='png')

    plt.clf()

